// ignore_for_file: lines_longer_than_80_chars

import 'dart:convert';

import 'package:chia_crypto_utils/chia_crypto_utils.dart';
import 'package:chia_crypto_utils/src/cat/exceptions/invalid_cat_exception.dart';
import 'package:test/test.dart';

Future<void> main() async {
  final assetId = Puzzlehash.fromHex(
    '625c2184e97576f5df1be46c15b2b8771c79e4e6f0aa42d3bfecaebe733f4b8c',
  );
  const catParentCoinSpendJson =
      '{"coin": {"parent_coin_info": "f4151ea2a45da9a31c748f71c866f27d17b91b605aeafa4089c35c685be72f77", "puzzle_hash": "778fd03bdb07f0f99046738bc6981dafacbbc0c09fecce04006576f301b74b12", "amount": 100}, "puzzle_reveal": "", "solution": ""}';
  final catParentCoinSpend = CoinSpend.fromJson(
    jsonDecode(catParentCoinSpendJson) as Map<String, dynamic>,
  );
  final validCatCoin = Coin(
    confirmedBlockIndex: 17409283,
    spentBlockIndex: 0,
    coinbase: false,
    timestamp: 2748299274,
    parentCoinInfo: Bytes.fromHex(
      'c1fdd54dd268a26fde78bb203a32a14ca942f015a9343d4ea5e9961f997256a1',
    ),
    puzzlehash: Puzzlehash.fromHex(
      '778fd03bdb07f0f99046738bc6981dafacbbc0c09fecce04006576f301b74b12',
    ),
    amount: 200,
  );

  const standardCoinParentSpendJson =
      '{"coin": {"parent_coin_info": "88ab5f0a47c96b6dd8e0faa86f6f7711730c7a0f30aaa080a62b27139fa3fcb6", "puzzle_hash": "0b7a3d5e723e0b046fd51f95cabf2d3e2616f05d9d1833e8166052b43d9454ad", "amount": 10000}, "puzzle_reveal": "", "solution": "ff80ffff01ffff33ffa00b7a3d5e723e0b046fd51f95cabf2d3e2616f05d9d1833e8166052b43d9454adff82232880ffff34ff8203e880ffff3dffa0ece7df847532f6bebd02f1031133fea2f051d67bd8731b197a0efaae09bf09d180ffff3cffa043512694375995ddd59daf7983537433623c65f027522021a58f7f83dba3420e8080ff8080"}';
  final standardCoinParentSpend = CoinSpend.fromJson(
    jsonDecode(standardCoinParentSpendJson) as Map<String, dynamic>,
  );
  final standardCoin = Coin(
    confirmedBlockIndex: 16409283,
    spentBlockIndex: 0,
    coinbase: false,
    timestamp: 274829924,
    parentCoinInfo: Bytes.fromHex(
      '0ea1f9522c7e365cbc502fa6d7ae90d8a5e43a6e983d0e25797094f0110f19cf',
    ),
    puzzlehash: Puzzlehash.fromHex(
      '0b7a3d5e723e0b046fd51f95cabf2d3e2616f05d9d1833e8166052b43d9454ad',
    ),
    amount: 9000,
  );

  test('Does not error on valid cat', () async {
    final catCoinObject = CatCoin(coin: validCatCoin, parentCoinSpend: catParentCoinSpend);
    expect(catCoinObject.assetId, assetId);
  });

  test('errors on invalid cat', () async {
    expect(
      () => CatCoin(coin: standardCoin, parentCoinSpend: standardCoinParentSpend),
      throwsA(isA<InvalidCatException>()),
    );
  });
}
